	continue;*/

operation.search:
// If this is the one we are acting on, can we even act?
if ((!$context['can_manage_protected'] && $row['group_type'] == 1) || (!$context['can_manage_membergroups'] && $row['group_type'] == 0))
	$canChangePrimary = false;

